import { FcDeleteDatabase } from "react-icons/fc";
import { useDispatch } from "react-redux";
import { remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast";

const CartItem = ({ item, itemIndex }) => {
  const dispatch = useDispatch();

  const removeFromCart = () => {
    dispatch(remove(item.id));
    toast.success("Item Removed");
  };

  return (
    // <div className="border p-4 mb-4 grid grid-cols-3 gap-2">
    <div className="container mx-auto mt-10 border p-4 mb-4 grid grid-cols-3 gap-2">
      <div className="col-span-1">
        <img src={item.image} className="w-160 h-160 object-cover" alt={item.title} />
      </div>
      <div className="col-span-2">
        <h1 className="text-lg font-bold">{item.title}</h1>
        <p className="text-gray-600">{item.description}</p>
        <div className="flex items-center">
          <p className="text-base font-bold">${item.price}</p>
          <div
            className="cursor-pointer ml-4 text-red-500"
            onClick={removeFromCart}
          >
            <FcDeleteDatabase size={20} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
